import React from 'react';

const EmailItem = ({ email }) => {
  return (
    <div className="email-item">
      <h3>{email.subject}</h3>
      <p><strong>From:</strong> {email.sender}</p>
      <p>{email.body}</p>
      <hr />
    </div>
  );
};

export default EmailItem;
